import React, { useMemo, useState, useCallback } from 'react';
import { 
  createEditor, 
  Descendant, 
  Editor, 
  Element as SlateElement,
  Node as SlateNode,
  Transforms,
  Text
} from 'slate';
import { Slate, Editable, withReact, RenderLeafProps } from 'slate-react';
import { withHistory } from 'slate-history';
import { useAutoSave } from '../../hooks/useAutoSave';
import Toolbar from './Toolbar';
import { CustomElement, CustomText, BlockFormatType } from './types';

interface EditorProps {
  onThemeChange?: (isDark: boolean) => void;
}

// 自定义渲染叶子节点
const Leaf = ({ attributes, children, leaf }: RenderLeafProps) => {
  if (leaf.bold) {
    children = <strong>{children}</strong>;
  }
  if (leaf.italic) {
    children = <em>{children}</em>;
  }
  if (leaf.underline) {
    children = <u>{children}</u>;
  }
  return <span {...attributes}>{children}</span>;
};

const NoteEditor: React.FC<EditorProps> = ({ onThemeChange }) => {
  const editor = useMemo(() => withHistory(withReact(createEditor())), []);

  const [value, setValue] = useState<Descendant[]>([
    {
      type: 'paragraph',
      children: [{ text: '' }],
    },
  ]);

  // 转换块类型
  const toggleBlock = (format: BlockFormatType) => {
    const isActive = isBlockActive(format);
    const isList = ['numbered-list', 'bulleted-list'].includes(format);

    Transforms.unwrapNodes(editor, {
      match: n => 
        !Editor.isEditor(n) &&
        SlateElement.isElement(n) &&
        ['numbered-list', 'bulleted-list'].includes(n.type),
      split: true,
    });

    const newProperties: Partial<SlateElement> = {
      type: isActive ? 'paragraph' : isList ? 'list-item' : format,
    };

    Transforms.setNodes<SlateElement>(editor, newProperties);

    if (!isActive && isList) {
      const block = { type: format, children: [] };
      Transforms.wrapNodes(editor, block);
    }
  };

  // 检查块类型是否激活
  const isBlockActive = (format: BlockFormatType) => {
    const [match] = Editor.nodes(editor, {
      match: n =>
        !Editor.isEditor(n) && SlateElement.isElement(n) && n.type === format,
    });
    return !!match;
  };

  // 渲染元素
  const renderElement = useCallback((props: any) => {
    switch (props.element.type) {
      case 'bulleted-list':
        return <ul {...props.attributes}>{props.children}</ul>;
      case 'numbered-list':
        return <ol {...props.attributes}>{props.children}</ol>;
      case 'list-item':
        return <li {...props.attributes}>{props.children}</li>;
      default:
        return <p {...props.attributes}>{props.children}</p>;
    }
  }, []);

  // 导出功能保持不变
  const handleExport = useCallback((format: 'txt' | 'md' | 'html') => {
    const text = value.map(n => SlateNode.string(n)).join('\n');
    const blob = new Blob([text], { type: 'text/plain;charset=utf-8' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    const timestamp = new Date().toISOString().replace(/[:.]/g, '-');
    a.download = `note-${timestamp}.${format}`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  }, [value]);

  return (
    <div className="h-full flex flex-col">
      <Slate
        editor={editor}
        value={value}
        onChange={newValue => setValue(newValue)}
      >
        <Toolbar 
          onExport={handleExport} 
          onThemeChange={onThemeChange}
          onToggleBlock={toggleBlock}
          isBlockActive={isBlockActive}
        />
        <div className="flex-1 p-4 overflow-auto">
          <Editable
            className="prose max-w-none dark:prose-invert"
            placeholder="Start typing..."
            renderLeaf={Leaf}
            renderElement={renderElement}
            onKeyDown={event => {
              if (!event.ctrlKey) return;
              switch (event.key) {
                case 'b': {
                  event.preventDefault();
                  Editor.addMark(editor, 'bold', true);
                  break;
                }
                case 'i': {
                  event.preventDefault();
                  Editor.addMark(editor, 'italic', true);
                  break;
                }
                case 'u': {
                  event.preventDefault();
                  Editor.addMark(editor, 'underline', true);
                  break;
                }
              }
            }}
          />
        </div>
      </Slate>
    </div>
  );
};

export default NoteEditor;
