import React, { useMemo, useState, useCallback } from 'react';
import { 
  createEditor, 
  Descendant, 
  Editor, 
  Element as SlateElement,
  Node as SlateNode,
  Transforms,
  Text
} from 'slate';
import { Slate, Editable, withReact, RenderLeafProps } from 'slate-react';
import { withHistory } from 'slate-history';
import { useAutoSave } from '../../hooks/useAutoSave';
import Toolbar from './Toolbar';
import { CustomElement, CustomText } from './types';

interface EditorProps {
  onThemeChange?: (isDark: boolean) => void;
}

// 自定义渲染叶子节点
const Leaf = ({ attributes, children, leaf }: RenderLeafProps) => {
  if (leaf.bold) {
    children = <strong>{children}</strong>;
  }
  if (leaf.italic) {
    children = <em>{children}</em>;
  }
  if (leaf.underline) {
    children = <u>{children}</u>;
  }
  return <span {...attributes}>{children}</span>;
};

const NoteEditor: React.FC<EditorProps> = ({ onThemeChange }) => {
  const editor = useMemo(() => {
    const e = withHistory(withReact(createEditor()));

    // 重写 insertText 来处理列表
    const { insertText } = e;
    e.insertText = text => {
      if (text === '\n• ' || text === '\n1. ') {
        // 创建新的列表项
        const listType = text === '\n• ' ? 'bulleted-list' : 'numbered-list';
        Transforms.insertNodes(e, {
          type: listType,
          children: [{ text: '' }],
        });
        return;
      }
      insertText(text);
    };

    return e;
  }, []);

  const [value, setValue] = useState<Descendant[]>([
    {
      type: 'paragraph',
      children: [{ text: '' }],
    },
  ]);

  const handleExport = useCallback((format: 'txt' | 'md' | 'html') => {
    const text = value.map(n => SlateNode.string(n)).join('\n');
    const blob = new Blob([text], { type: 'text/plain;charset=utf-8' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    const timestamp = new Date().toISOString().replace(/[:.]/g, '-');
    a.download = `note-${timestamp}.${format}`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  }, [value]);

  // 渲染元素
  const renderElement = useCallback((props: any) => {
    switch (props.element.type) {
      case 'bulleted-list':
        return <ul {...props.attributes}>{props.children}</ul>;
      case 'numbered-list':
        return <ol {...props.attributes}>{props.children}</ol>;
      default:
        return <p {...props.attributes}>{props.children}</p>;
    }
  }, []);

  return (
    <div className="h-full flex flex-col">
      <Slate
        editor={editor}
        value={value}
        onChange={newValue => setValue(newValue)}
      >
        <Toolbar onExport={handleExport} onThemeChange={onThemeChange} />
        <div className="flex-1 p-4 overflow-auto">
          <Editable
            className="prose max-w-none dark:prose-invert"
            placeholder="Start typing..."
            renderLeaf={Leaf}
            renderElement={renderElement}
            onKeyDown={event => {
              if (!event.ctrlKey) return;
              switch (event.key) {
                case 'b': {
                  event.preventDefault();
                  Editor.addMark(editor, 'bold', true);
                  break;
                }
                case 'i': {
                  event.preventDefault();
                  Editor.addMark(editor, 'italic', true);
                  break;
                }
                case 'u': {
                  event.preventDefault();
                  Editor.addMark(editor, 'underline', true);
                  break;
                }
              }
            }}
          />
        </div>
      </Slate>
    </div>
  );
};

export default NoteEditor;
