import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Section, Note } from '../../types';
import { addSection, addNote, setCurrentSection, setCurrentNote } from '../../store/notebookSlice';

const SectionList: React.FC = () => {
  const dispatch = useDispatch();
  const currentNotebook = useSelector((state: any) => state.notebooks.currentNotebook);
  const sections = useSelector((state: any) => 
    state.notebooks.items
      .find((nb: any) => nb.id === currentNotebook?.id)?.sections || []
  );

  const handleAddSection = () => {
    if (!currentNotebook) return;
    
    const newSection: Section = {
      id: `section-${Date.now()}`,
      title: 'New Section',
      notes: [],
      notebookId: currentNotebook.id
    };

    dispatch(addSection({ notebookId: currentNotebook.id, section: newSection }));
  };

  const handleAddNote = (sectionId: string) => {
    const newNote: Note = {
      id: `note-${Date.now()}`,
      title: 'New Note',
      content: [{ type: 'paragraph', children: [{ text: '' }] }],
      sectionId,
      tags: [],
      createdAt: new Date(),
      updatedAt: new Date()
    };

    dispatch(addNote({ sectionId, note: newNote }));
  };

  const handleSelectSection = (section: Section) => {
    dispatch(setCurrentSection(section));
  };

  const handleSelectNote = (note: Note) => {
    dispatch(setCurrentNote(note));
  };

  return (
    <div className="h-full flex flex-col">
      <div className="flex justify-between items-center p-4 border-b">
        <h2 className="text-lg font-semibold">Sections</h2>
        <button
          className="px-3 py-1 bg-blue-500 text-white rounded hover:bg-blue-600"
          onClick={handleAddSection}
        >
          + New
        </button>
      </div>
      
      <div className="flex-1 overflow-auto">
        {sections.map((section: Section) => (
          <div key={section.id} className="border-b">
            <div
              className="flex justify-between items-center p-3 hover:bg-gray-100 cursor-pointer"
              onClick={() => handleSelectSection(section)}
            >
              <span className="font-medium">{section.title}</span>
              <button
                className="text-sm text-blue-500 hover:text-blue-600"
                onClick={(e) => {
                  e.stopPropagation();
                  handleAddNote(section.id);
                }}
              >
                + Note
              </button>
            </div>
            
            <div className="pl-6">
              {section.notes.map((note: Note) => (
                <div
                  key={note.id}
                  className="p-2 hover:bg-gray-100 cursor-pointer"
                  onClick={() => handleSelectNote(note)}
                >
                  {note.title}
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default SectionList;
