import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { Section, Note } from '../../types';
import {
  addSection,
  addNote,
  setCurrentSection,
  setCurrentNote,
  updateSectionTitle,
  updateNoteTitle,
  deleteSection,
  deleteNote,
  reorderSections,
  reorderNotes
} from '../../store/notebookSlice';

interface EditableItemProps {
  value: string;
  onSave: (newValue: string) => void;
  isEditing: boolean;
  setIsEditing: (value: boolean) => void;
}

const EditableItem: React.FC<EditableItemProps> = ({
  value,
  onSave,
  isEditing,
  setIsEditing
}) => {
  const [editValue, setEditValue] = useState(value);

  const handleSave = () => {
    if (editValue.trim()) {
      onSave(editValue);
      setIsEditing(false);
    }
  };

  if (isEditing) {
    return (
      <input
        type="text"
        value={editValue}
        onChange={(e) => setEditValue(e.target.value)}
        onBlur={handleSave}
        onKeyDown={(e) => e.key === 'Enter' && handleSave()}
        className="px-2 py-1 w-full border rounded"
        autoFocus
      />
    );
  }

  return (
    <span onDoubleClick={() => setIsEditing(true)}>
      {value}
    </span>
  );
};

const SectionList: React.FC = () => {
  const dispatch = useDispatch();
  const currentNotebook = useSelector((state: any) => state.notebooks.currentNotebook);
  const sections = useSelector((state: any) => 
    state.notebooks.items
      .find((nb: any) => nb.id === currentNotebook?.id)?.sections || []
  );

  const [editingSectionId, setEditingSectionId] = useState<string | null>(null);
  const [editingNoteId, setEditingNoteId] = useState<string | null>(null);

  // 添加 Section
  const handleAddSection = () => {
    if (!currentNotebook) {
      alert('Please select a notebook first');
      return;
    }
    
    const newSection: Section = {
      id: `section-${Date.now()}`,
      title: 'New Section',
      notes: [],
      notebookId: currentNotebook.id
    };

    dispatch(addSection({ notebookId: currentNotebook.id, section: newSection }));
  };

  // 添加 Note
  const handleAddNote = (sectionId: string) => {
    const newNote: Note = {
      id: `note-${Date.now()}`,
      title: 'New Note',
      content: [{ type: 'paragraph', children: [{ text: '' }] }],
      sectionId,
      tags: [],
      createdAt: new Date(),
      updatedAt: new Date()
    };

    dispatch(addNote({ sectionId, note: newNote }));
  };

  // 处理拖拽结束
  const onDragEnd = (result: any) => {
    const { source, destination, type } = result;

    if (!destination) return;

    if (type === 'section') {
      dispatch(reorderSections({
        notebookId: currentNotebook.id,
        startIndex: source.index,
        endIndex: destination.index
      }));
    } else if (type === 'note') {
      const [sectionId] = source.droppableId.split('-');
      dispatch(reorderNotes({
        sectionId,
        startIndex: source.index,
        endIndex: destination.index
      }));
    }
  };

  // 处理重命名
  const handleRenameSection = (sectionId: string, newTitle: string) => {
    dispatch(updateSectionTitle({ sectionId, title: newTitle }));
  };

  const handleRenameNote = (noteId: string, newTitle: string) => {
    dispatch(updateNoteTitle({ noteId, title: newTitle }));
  };

  // 处理删除
  const handleDeleteSection = (sectionId: string) => {
    if (window.confirm('Are you sure you want to delete this section?')) {
      dispatch(deleteSection({ notebookId: currentNotebook.id, sectionId }));
    }
  };

  const handleDeleteNote = (noteId: string) => {
    if (window.confirm('Are you sure you want to delete this note?')) {
      dispatch(deleteNote({ noteId }));
    }
  };

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <div className="h-full flex flex-col">
        <div className="flex justify-between items-center p-4 border-b">
          <h2 className="text-lg font-semibold">Sections</h2>
          <button
            className="px-3 py-1 bg-blue-500 text-white rounded hover:bg-blue-600"
            onClick={handleAddSection}
          >
            + New
          </button>
        </div>
        
        <Droppable droppableId="sections" type="section">
          {(provided) => (
            <div
              className="flex-1 overflow-auto"
              ref={provided.innerRef}
              {...provided.droppableProps}
            >
              {sections.map((section: Section, index: number) => (
                <Draggable
                  key={section.id}
                  draggableId={section.id}
                  index={index}
                >
                  {(provided) => (
                    <div
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      className="border-b"
                    >
                      <div
                        className="flex justify-between items-center p-3 hover:bg-gray-100"
                        {...provided.dragHandleProps}
                      >
                        <div className="flex-1">
                          <EditableItem
                            value={section.title}
                            onSave={(newTitle) => handleRenameSection(section.id, newTitle)}
                            isEditing={editingSectionId === section.id}
                            setIsEditing={(value) => setEditingSectionId(value ? section.id : null)}
                          />
                        </div>
                        <div className="flex gap-2">
                          <button
                            className="text-sm text-blue-500 hover:text-blue-600"
                            onClick={(e) => {
                              e.stopPropagation();
                              handleAddNote(section.id);
                            }}
                          >
                            + Note
                          </button>
                          <button
                            className="text-sm text-red-500 hover:text-red-600"
                            onClick={(e) => {
                              e.stopPropagation();
                              handleDeleteSection(section.id);
                            }}
                          >
                            Delete
                          </button>
                        </div>
                      </div>
                      
                      <Droppable droppableId={`${section.id}-notes`} type="note">
                        {(provided) => (
                          <div
                            ref={provided.innerRef}
                            {...provided.droppableProps}
                            className="pl-6"
                          >
                            {section.notes.map((note: Note, index: number) => (
                              <Draggable
                                key={note.id}
                                draggableId={note.id}
                                index={index}
                              >
                                {(provided) => (
                                  <div
                                    ref={provided.innerRef}
                                    {...provided.draggableProps}
                                    {...provided.dragHandleProps}
                                    className="flex justify-between items-center p-2 hover:bg-gray-100"
                                  >
                                    <div className="flex-1">
                                      <EditableItem
                                        value={note.title}
                                        onSave={(newTitle) => handleRenameNote(note.id, newTitle)}
                                        isEditing={editingNoteId === note.id}
                                        setIsEditing={(value) => setEditingNoteId(value ? note.id : null)}
                                      />
                                    </div>
                                    <button
                                      className="text-sm text-red-500 hover:text-red-600 opacity-0 group-hover:opacity-100"
                                      onClick={() => handleDeleteNote(note.id)}
                                    >
                                      Delete
                                    </button>
                                  </div>
                                )}
                              </Draggable>
                            ))}
                            {provided.placeholder}
                          </div>
                        )}
                      </Droppable>
                    </div>
                  )}
                </Draggable>
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </div>
    </DragDropContext>
  );
};

export default SectionList;
