import React, { useCallback, useState } from 'react';
import { useSlate } from 'slate-react';
import { 
  Editor, 
  Element as SlateElement, 
  BaseEditor,
  NodeEntry,
  Node
} from 'slate';
import { CustomElement, CustomText, MarkFormat } from './types';

interface ToolbarProps {
  onExport?: (format: 'txt' | 'md' | 'html') => void;
  onThemeChange?: (isDark: boolean) => void;
}

type CustomEditor = BaseEditor & {
  setNodes: (editor: CustomEditor, props: Partial<CustomElement>, options?: any) => void;
};

const Toolbar: React.FC<ToolbarProps> = ({ onExport, onThemeChange }) => {
  const editor = useSlate() as CustomEditor;
  const [isDarkMode, setIsDarkMode] = useState(false);

  // 格式化按钮处理函数
  const toggleFormat = useCallback((format: MarkFormat) => {
    const isActive = isFormatActive(format);
    if (isActive) {
      Editor.removeMark(editor, format);
    } else {
      Editor.addMark(editor, format, true);
    }
  }, [editor]);

  // 检查格式是否激活
  const isFormatActive = (format: MarkFormat) => {
    const marks = Editor.marks(editor) as Partial<CustomText> | null;
    return marks ? marks[format] === true : false;
  };

  // 切换列表格式
  const toggleList = useCallback((type: 'numbered-list' | 'bulleted-list') => {
    const isList = isBlockActive(type);

    editor.setNodes(
      editor,
      { type: isList ? 'paragraph' : type },
      { match: (n: Node) => SlateElement.isElement(n) && Editor.isBlock(editor, n) }
    );
  }, [editor]);

  // 检查块级格式是否激活
  const isBlockActive = (type: string) => {
    const matches = Array.from(Editor.nodes(editor, {
      match: (n: Node) => SlateElement.isElement(n) && (n as CustomElement).type === type,
    }));
    return matches.length > 0;
  };

  // 切换主题
  const toggleTheme = useCallback(() => {
    const newMode = !isDarkMode;
    setIsDarkMode(newMode);
    onThemeChange?.(newMode);
  }, [isDarkMode, onThemeChange]);

  interface FormatButtonProps {
    format: string;
    icon: string;
    onClick: () => void;
    isActive: boolean;
  }

  // 格式化按钮组件
  const FormatButton: React.FC<FormatButtonProps> = ({ format, icon, onClick, isActive }) => (
    <button
      className={`p-2 rounded hover:bg-gray-100 dark:hover:bg-gray-700 ${
        isActive ? 'text-blue-500' : ''
      }`}
      onClick={onClick}
    >
      <span className="material-icons">{icon}</span>
    </button>
  );

  return (
    <div className="flex items-center gap-2 p-2 border-b dark:border-gray-700">
      <div className="flex gap-1">
        <FormatButton
          format="bold"
          icon="format_bold"
          onClick={() => toggleFormat('bold')}
          isActive={isFormatActive('bold')}
        />
        <FormatButton
          format="italic"
          icon="format_italic"
          onClick={() => toggleFormat('italic')}
          isActive={isFormatActive('italic')}
        />
        <FormatButton
          format="underline"
          icon="format_underlined"
          onClick={() => toggleFormat('underline')}
          isActive={isFormatActive('underline')}
        />

        <div className="w-px bg-gray-300 dark:bg-gray-700 mx-2" />

        <FormatButton
          format="bulleted-list"
          icon="format_list_bulleted"
          onClick={() => toggleList('bulleted-list')}
          isActive={isBlockActive('bulleted-list')}
        />
        <FormatButton
          format="numbered-list"
          icon="format_list_numbered"
          onClick={() => toggleList('numbered-list')}
          isActive={isBlockActive('numbered-list')}
        />
      </div>

      <div className="flex-1" />

      <div className="flex gap-2">
        <button
          className="p-2 rounded hover:bg-gray-100 dark:hover:bg-gray-700"
          onClick={() => onExport?.('txt')}
        >
          <span className="material-icons">download</span>
        </button>

        <button
          className="p-2 rounded hover:bg-gray-100 dark:hover:bg-gray-700"
          onClick={toggleTheme}
        >
          <span className="material-icons">
            {isDarkMode ? 'light_mode' : 'dark_mode'}
          </span>
        </button>
      </div>
    </div>
  );
};

export default Toolbar;
