import React from 'react';
import { useSlate } from 'slate-react';
import { Editor, Element as SlateElement } from 'slate';

// 定义格式化按钮接口
interface FormatButtonProps {
  format: string;
  icon: string;
  onClick: () => void;
  isActive?: boolean;
}

const FormatButton: React.FC<FormatButtonProps> = ({ format, icon, onClick, isActive }) => (
  <button
    className={`p-1 rounded ${
      isActive ? 'bg-gray-200' : 'hover:bg-gray-100'
    }`}
    onMouseDown={(e) => {
      e.preventDefault();
      onClick();
    }}
    title={format}
  >
    <span className="material-icons">{icon}</span>
  </button>
);

const Toolbar: React.FC = () => {
  const editor = useSlate();

  // 检查当前选中文本的格式
  const isFormatActive = (format: string) => {
    const marks = Editor.marks(editor);
    return marks ? marks[format] === true : false;
  };

  // 切换文本格式
  const toggleFormat = (format: string) => {
    const isActive = isFormatActive(format);
    if (isActive) {
      Editor.removeMark(editor, format);
    } else {
      Editor.addMark(editor, format, true);
    }
  };

  // 切换列表格式
  const toggleList = (format: 'numbered-list' | 'bulleted-list') => {
    const isList = (n: any) => 
      !Editor.isEditor(n) && 
      SlateElement.isElement(n) && 
      n.type === format;

    const isActive = Editor.nodes(editor, {
      match: isList,
    }).next().done;

    Editor.setNodes(
      editor,
      { type: isActive ? 'paragraph' : format },
      { match: n => Editor.isBlock(editor, n) }
    );
  };

  return (
    <div className="border-b p-2 flex gap-2">
      <FormatButton
        format="bold"
        icon="format_bold"
        onClick={() => toggleFormat('bold')}
        isActive={isFormatActive('bold')}
      />
      <FormatButton
        format="italic"
        icon="format_italic"
        onClick={() => toggleFormat('italic')}
        isActive={isFormatActive('italic')}
      />
      <FormatButton
        format="underline"
        icon="format_underlined"
        onClick={() => toggleFormat('underline')}
        isActive={isFormatActive('underline')}
      />
      
      <div className="w-px bg-gray-300 mx-2" />
      
      <FormatButton
        format="bulleted-list"
        icon="format_list_bulleted"
        onClick={() => toggleList('bulleted-list')}
      />
      <FormatButton
        format="numbered-list"
        icon="format_list_numbered"
        onClick={() => toggleList('numbered-list')}
      />
    </div>
  );
};

export default Toolbar;
