import { Node } from 'slate';
import { CustomElement, NoteStats } from '../components/Editor/types';

export function calculateNoteStats(nodes: CustomElement[]): NoteStats {
  let wordCount = 0;
  let characterCount = 0;
  let codeBlockCount = 0;
  let tableCount = 0;

  const countStats = (node: Node) => {
    if (Node.string(node)) {
      const text = Node.string(node);
      characterCount += text.length;
      wordCount += text.trim().split(/\s+/).filter(Boolean).length;
    }
    
    if ('type' in node) {
      if (node.type === 'code-block') codeBlockCount++;
      if (node.type === 'table') tableCount++;
    }
  };

  nodes.forEach(node => {
    Node.visit(node, countStats);
  });

  // Estimate reading time (average reading speed: 200 words per minute)
  const readingTime = Math.ceil(wordCount / 200);

  return {
    wordCount,
    characterCount,
    readingTime,
    codeBlockCount,
    tableCount,
    lastModified: new Date()
  };
}
